import { useState, useEffect } from 'react';
import './App.css';

function App() {

  const [isLoading, setIsLoading] = useState(true);
  const [title, setTitle] = useState('');
  const [image, setImage] = useState('');
  const [text, setText] = useState('');

  /* get all data link */

  function setData({ data }) {
    setImage(data.image);
    setText(data.text);
    setTitle(data.title);
  }

  async function getData() {
    let response = await fetch('https://wickedalice.github.io/data.json');
    let data = await response.json();
    return data;
  }

  useEffect(() => {
    getData().then(data => {
      setData(data)
    }).finally(() => {
      setIsLoading(false);
    });

    // fetch()
    //   .then((response) => {
    //     if (response.ok) {
    //       response.json().then(data => setData(data));
    //     } else {
    //       console.log(response);
    //     }
    //   })
    //   .catch(error => {
    //     console.log(error);
    //   })
    //   .finally(() => {
    //     setIsLoading(false);
    //   })
  }, []);

  return (
    <> {isLoading
      ? <div></div>
      : <a className="Banner"
           href="https://intelligence.weforum.org"
           target="_blank"
           rel="noopener noreferrer">
        <header className="Banner-header">
          <text>Explore context</text>
          <div className="Banner-header-icon" />
        </header>
        <div className="Banner-title">
          <text> {title} </text>
        </div>
        <img src={image} className="Banner-map" alt="Banner-map"/>
        <div className="Banner-text">
          {text}
        </div>
      </a>
    }
    </>
  );
}

export default App;
