import React, { useState, useEffect } from 'react';
import './App.css';

const LAYOUT_OPTIONS = {
  vertical: 'vertical',
  horizontal: 'horizontal'
}

function App({id, lang, layout}) {

  const [isLoading, setIsLoading] = useState(true);
  const [title, setTitle] = useState('');
  const [image, setImage] = useState('');
  const [text, setText] = useState('');

  console.log(id);
  console.log(lang);
  console.log(layout);
  const [layoutOrientation, setLayoutOrientation] = useState(layout || LAYOUT_OPTIONS.vertical);

  /* get all data link */

  function setData({ data }) {
    setImage(data.image);
    setText(data.text);
    setTitle(data.title);
  }

  async function getData() {
    let response = await fetch('https://wickedalice.github.io/data.json');
    let data = await response.json();
    return data;
  }

  useEffect(() => {
    getData().then(data => {
      setData(data)
    }).finally(() => {
      setIsLoading(false);
    });

    // fetch()
    //   .then((response) => {
    //     if (response.ok) {
    //       response.json().then(data => setData(data));
    //     } else {
    //       console.log(response);
    //     }
    //   })
    //   .catch(error => {
    //     console.log(error);
    //   })
    //   .finally(() => {
    //     setIsLoading(false);
    //   })
  }, []);

  return (
    <a className="Banner"
       href="https://intelligence.weforum.org"
       target="_blank"
       rel="noopener noreferrer">
      {isLoading
        ? <div className="Banner-loader"/>
        : (layoutOrientation === LAYOUT_OPTIONS.vertical)
          ? <div className="Banner-vertical">
            <div className="Banner-header">
              <span>Explore context</span>
              <div className="Banner-header-icon"/>
            </div>
            <div className="Banner-title">
              {title}
            </div>
            <img src={image} className="Banner-map" alt="Banner-map"/>
            <div className="Banner-text">
              {text}
            </div>
          </div>
            : <div className="Banner-horizontal">
            <div className="Banner-info">
              <div className="Banner-header">
                <span>Explore context</span>
                <div className="Banner-header-icon"/>
              </div>
              <div className="Banner-title">
                {title}
              </div>
              <div className="Banner-text">
                {text}
              </div>
            </div>
            <img src={image} className="Banner-map" alt="Banner-map"/>

            </div>
      }
    </a>

  );
}

export default App;
